print("SimulateChain")

chain <- ""

print(chain)

chain <- strsplit(chain, "")[[1]]

r <- 0
n <- 0
s <- 0

for (state in chain) {
  	if(state == "R"){
  		r = r+1
  	}else if(state == "N"){
  		n = n+1
 	}else if(state == "S"){
 		s = s+1
 	}
}

times <- c(r,n,s)
fraction <- round(times/length(chain),digits=3)

state_table <- data.frame(times, fraction)
print(state_table)

